import{AbelianSandpile}from"./class.min.min.mjs";import{Applet}from"/scripts/src/applets.min.min.mjs";import{showPage}from"/scripts/src/load-page.min.min.mjs";import{$}from"/scripts/src/main.min.min.mjs";function load(){const e=new AbelianSandpile({canvas:$("#output-canvas")}),t=$("#num-grains-input"),s=$("#computations-per-frame-input");function n(){var n=parseInt(t.value||1e4),a=parseInt(s.value||25);e.run({numGrains:n,computationsPerFrame:a})}Applet.listenToInputElements([t,s],n),e.setInputCaps([t,s],[1e6,20]),$("#generate-button").addEventListener("click",n),$("#download-button").addEventListener("click",()=>e.wilson.downloadFrame("an-abelian-sandpile.png")),showPage()}export{load};