import{AbelianSandpile}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const applet=new AbelianSandpile($("#output-canvas")),numGrainsInputElement=$("#num-grains-input"),computationsPerFrameInputElement=$("#computations-per-frame-input");function run(){var numGrains=parseInt(numGrainsInputElement.value||1e4),computationsPerFrame=parseInt(computationsPerFrameInputElement.value||25);applet.run({numGrains:numGrains,computationsPerFrame:computationsPerFrame})}applet.listenToInputElements([numGrainsInputElement,computationsPerFrameInputElement],run),applet.setInputCaps([numGrainsInputElement,computationsPerFrameInputElement],[1e6,20]),$("#generate-button").addEventListener("click",run),$("#download-button").addEventListener("click",()=>applet.wilson.downloadFrame("an-abelian-sandpile.png")),showPage()}export{load};