import{BrownianTree}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const applet=new BrownianTree($("#output-canvas"));$("#generate-button").addEventListener("click",run);const resolutionInputElement=$("#resolution-input");function run(){var resolution=parseInt(resolutionInputElement.value||1e3);applet.run({resolution:resolution})}applet.listenToInputElements([resolutionInputElement],run),applet.setInputCaps([resolutionInputElement],[2e3]),$("#download-button").addEventListener("click",()=>applet.wilson.downloadFrame("a-brownian-tree.png")),showPage()}export{load};