import{CalcudokuGenerator}from"./class.min.mjs";import{Applet}from"/scripts/src/applets.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const t=new CalcudokuGenerator({canvas:$("#output-canvas")});$("#generate-button").addEventListener("click",e);const r=$("#grid-size-input"),s=$("#max-cage-size-input");function e(){var e=parseInt(r.value||6),a=parseInt(s.value||1e3);t.run({gridSize:e,maxCageSize:a})}Applet.listenToInputElements([r,s],e),t.setInputCaps([r,s],[10,5]),$("#download-button").addEventListener("click",()=>{t.drawGrid(!0),t.wilson.downloadFrame("a-calcudoku-puzzle.png"),t.drawGrid(!1)}),showPage()}export{load};