import{CalcudokuGenerator}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const applet=new CalcudokuGenerator($("#output-canvas"));$("#generate-button").addEventListener("click",run);const gridSizeInputElement=$("#grid-size-input"),maxCageSizeInputElement=$("#max-cage-size-input");function run(){var gridSize=parseInt(gridSizeInputElement.value||6),maxCageSize=parseInt(maxCageSizeInputElement.value||1e3);applet.run({gridSize:gridSize,maxCageSize:maxCageSize})}applet.listenToInputElements([gridSizeInputElement,maxCageSizeInputElement],run),applet.setInputCaps([gridSizeInputElement,maxCageSizeInputElement],[10,5]),$("#download-button").addEventListener("click",()=>{applet.drawGrid(!0),applet.wilson.downloadFrame("a-calcudoku-puzzle.png"),applet.drawGrid(!1)}),showPage()}export{load};