import{CalcudokuGenerator}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const t=new CalcudokuGenerator($("#output-canvas"));$("#generate-button").addEventListener("click",e);const n=$("#grid-size-input"),o=$("#max-cage-size-input");function e(){var e=parseInt(n.value||6),a=parseInt(o.value||1e3);t.run({gridSize:e,maxCageSize:a})}t.listenToInputElements([n,o],e),t.setInputCaps([n,o],[10,5]),$("#download-button").addEventListener("click",()=>{t.drawGrid(!0),t.wilson.downloadFrame("a-calcudoku-puzzle.png"),t.drawGrid(!1)}),showPage()}export{load};