class ChaosGame extends Applet{resolution=500;webWorker=null;constructor(a){super(a);this.wilson=new Wilson(a,{renderer:"cpu",canvasWidth:this.resolution,canvasHeight:this.resolution,useFullscreen:!0,useFullscreenButton:!0,enterFullscreenButtonIconPath:"/graphics/general-icons/enter-fullscreen.png",exitFullscreenButtonIconPath:"/graphics/general-icons/exit-fullscreen.png"})}run(a,c){this.resolution=a;this.numVertices=c;this.wilson.changeCanvasSize(this.resolution,this.resolution);this.wilson.ctx.fillStyle=
"rgb(0, 0, 0)";this.wilson.ctx.fillRect(0,0,this.resolution,this.resolution);try{this.webWorker.terminate()}catch(b){}this.webWorker=new Worker(`/applets/chaos-game/scripts/worker.${DEBUG?"":"min."}js`);this.workers.push(this.webWorker);this.webWorker.onmessage=b=>this.wilson.render.drawFrame(b.data[0]);this.webWorker.postMessage([this.numVertices,this.resolution])}};
