import{ChaosGame}from"./class.min.min.mjs";import{Applet}from"/scripts/src/applets.min.min.mjs";import{showPage}from"/scripts/src/load-page.min.min.mjs";import{$}from"/scripts/src/main.min.min.mjs";function load(){const e=new ChaosGame({canvas:$("#output-canvas")});$("#generate-button").addEventListener("click",t);const n=$("#resolution-input"),o=$("#num-vertices-input");function t(){var t=parseInt(n.value||1e3),s=parseInt(o.value||5);e.run({resolution:t,numVertices:s})}Applet.listenToInputElements([n,o],t),e.setInputCaps([n],[2e3]),$("#download-button").addEventListener("click",()=>e.wilson.downloadFrame("a-chaos-game.png")),showPage()}export{load};