import{ChaosGame}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const n=new ChaosGame({canvas:$("#output-canvas")});$("#generate-button").addEventListener("click",e);const s=$("#resolution-input"),o=$("#num-vertices-input");function e(){var e=parseInt(s.value||1e3),t=parseInt(o.value||5);n.run({resolution:e,numVertices:t})}Applet.listenToInputElements([s,o],e),n.setInputCaps([s],[2e3]),$("#download-button").addEventListener("click",()=>n.wilson.downloadFrame("a-chaos-game.png")),showPage()}export{load};