import{ChaosGame}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const applet=new ChaosGame($("#output-canvas"));$("#generate-button").addEventListener("click",run);const resolutionInputElement=$("#resolution-input"),numVerticesInputElement=$("#num-vertices-input");function run(){var resolution=parseInt(resolutionInputElement.value||1e3),numVertices=parseInt(numVerticesInputElement.value||5);applet.run({resolution:resolution,numVertices:numVertices})}applet.listenToInputElements([resolutionInputElement,numVerticesInputElement],run),applet.setInputCaps([resolutionInputElement],[2e3]),$("#download-button").addEventListener("click",()=>applet.wilson.downloadFrame("a-chaos-game.png")),showPage()}export{load};