import{DominoShuffling}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const applet=new DominoShuffling($("#output-canvas")),resolutionInputElement=$("#resolution-input"),diamondSizeInputElement=$("#diamond-size-input");applet.listenToInputElements([resolutionInputElement,diamondSizeInputElement],run),applet.setInputCaps([resolutionInputElement,diamondSizeInputElement],[3e3,200]),$("#generate-button").addEventListener("click",run);const useSmoothColorsCheckboxElement=$("#use-smooth-colors-checkbox");function run(){var resolution=parseInt(resolutionInputElement.value||2e3),diamondSize=parseInt(diamondSizeInputElement.value||20)+1,useSmoothColors=useSmoothColorsCheckboxElement.checked;applet.run({resolution:resolution,diamondSize:diamondSize,useSmoothColors:useSmoothColors})}useSmoothColorsCheckboxElement.checked=!0,$("#download-button").addEventListener("click",()=>applet.wilson.downloadFrame("an-aztec-diamond.png")),showPage()}export{load};