import{DoublePendulumFractal}from"./class.min.mjs";import{changeOpacity}from"/scripts/src/animation.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const applet=new DoublePendulumFractal($("#output-canvas"),$("#pendulum-canvas")),resolutionInputElement=$("#resolution-input");applet.listenToInputElements([resolutionInputElement],run),applet.setInputCaps([resolutionInputElement],[2e3]),$("#generate-button").addEventListener("click",run);const switchPendulumCanvasButtonElement=$("#switch-pendulum-canvas-button");function run(){var resolution=parseInt(resolutionInputElement.value||1e3);applet.run({resolution:resolution})}switchPendulumCanvasButtonElement.addEventListener("click",()=>{applet.drawingFractal?(applet.drawingFractal=!1,changeOpacity(switchPendulumCanvasButtonElement,0).then(()=>{switchPendulumCanvasButtonElement.textContent="Return to Fractal",changeOpacity(switchPendulumCanvasButtonElement,1)})):(applet.drawingFractal=!0,applet.hidePendulumDrawerCanvas(),changeOpacity(switchPendulumCanvasButtonElement,0).then(()=>{switchPendulumCanvasButtonElement.textContent="Pick Pendulum",changeOpacity(switchPendulumCanvasButtonElement,1)}))}),$("#download-button").addEventListener("click",()=>applet.wilson.downloadFrame("the-double-pendulum-fractal.png")),showPage()}export{load};