function writeStateFromAscii(state,gridSize,ascii){ascii.replaceAll(/[\t\s]/g,"");var i=ascii.split("\n").filter(row=>0!==row.length),e=Math.floor(gridSize/2)+Math.floor(i.length/2),r=Math.floor(gridSize/2)-Math.floor(i[0].length/2);for(let l=0;l<i.length;l++)for(let t=0;t<i[l].length;t++)"."!==i[l][t]&&(state[gridSize*(e-l)+(r+t)]=1)}export{writeStateFromAscii};