import{JuliaSet}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){var e=$("#switch-julia-mode-button");const o=new JuliaSet($("#output-canvas"),e),t=$("#resolution-input");o.setInputCaps([t],[2e3]);var a=$("#double-precision-checkbox");t.addEventListener("input",()=>{o.resolution=parseInt(t.value||500),o.changeAspectRatio()}),e.addEventListener("click",()=>{o.advanceJuliaMode()}),a.addEventListener("input",()=>{o.toggleUseDoublePrecision()}),$("#download-button").addEventListener("click",()=>{0===o.juliaMode?o.wilson.downloadFrame("the-mandelbrot-set.png"):o.wilson.downloadFrame("a-julia-set.png")}),showPage()}export{load};