import{JuliaSet}from"./class.min.mjs";function load(){var e=$("#switch-julia-mode-button");const t=new JuliaSet($("#output-canvas"),e),n=$("#resolution-input");t.setInputCaps([n],[2e3]);var o=$("#double-precision-checkbox");n.addEventListener("input",()=>{t.resolution=parseInt(n.value||500),t.changeAspectRatio()}),e.addEventListener("click",()=>{t.advanceJuliaMode()}),o.addEventListener("input",()=>{t.toggleUseDoublePrecision()}),$("#download-button").addEventListener("click",()=>{0===t.juliaMode?t.wilson.downloadFrame("the-mandelbrot-set.png"):t.wilson.downloadFrame("a-julia-set.png")}),Page.show()}export{load};