import{JuliaSet}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){var switchJuliaModeButtonElement=$("#switch-julia-mode-button");const applet=new JuliaSet($("#output-canvas"),switchJuliaModeButtonElement),resolutionInputElement=$("#resolution-input");applet.setInputCaps([resolutionInputElement],[2e3]);var doublePrecisionCheckboxElement=$("#double-precision-checkbox");resolutionInputElement.addEventListener("input",()=>{applet.resolution=parseInt(resolutionInputElement.value||500),applet.changeAspectRatio()}),switchJuliaModeButtonElement.addEventListener("click",()=>{applet.advanceJuliaMode()}),doublePrecisionCheckboxElement.addEventListener("input",()=>{applet.toggleUseDoublePrecision()}),$("#download-button").addEventListener("click",()=>{0===applet.juliaMode?applet.wilson.downloadFrame("the-mandelbrot-set.png"):applet.wilson.downloadFrame("a-julia-set.png")}),showPage()}export{load};