import{KickedRotator}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const applet=new KickedRotator($("#output-canvas"));$("#generate-button").addEventListener("click",run);const resolutionInputElement=$("#resolution-input"),kInputElement=$("#k-input"),orbitSeparationInputElement=$("#orbit-separation-input");function run(){var resolution=parseInt(resolutionInputElement.value||1e3),K=parseFloat(kInputElement.value||.75),orbitSeparation=parseInt(orbitSeparationInputElement.value||0);applet.run({resolution:resolution,K:K,orbitSeparation:orbitSeparation})}applet.listenToInputElements([resolutionInputElement,kInputElement,orbitSeparationInputElement],run),applet.setInputCaps([resolutionInputElement,kInputElement],[1e3,2]),$("#download-button").addEventListener("click",()=>{applet.wilson.downloadFrame("a-kicked-rotator.png")}),showPage()}export{load};