import{KickedRotator}from"./class.min.mjs";import{Applet}from"/scripts/src/applets.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const a=new KickedRotator({canvas:$("#output-canvas")});$("#generate-button").addEventListener("click",t);const n=$("#resolution-input"),s=$("#k-input"),r=$("#orbit-separation-input");function t(){var t=parseInt(n.value||1e3),o=parseFloat(s.value||.75),e=parseInt(r.value||0);a.run({resolution:t,K:o,orbitSeparation:e})}Applet.listenToInputElements([n,s,r],t),a.setInputCaps([n,s],[1e3,2]),$("#download-button").addEventListener("click",()=>{a.wilson.downloadFrame("a-kicked-rotator.png")}),showPage()}export{load};