import{showPage}from"/scripts/src/load-page.min.mjs";import{LyapunovFractal}from"./class.min.mjs";function load(){const n=new LyapunovFractal($("#output-canvas"));function t(){var t=(a.value||"AB").toUpperCase();n.run(t)}const a=$("#generating-string-input");n.listenToInputElements([a],t),$("#generate-button").addEventListener("click",t);const e=$("#resolution-input");n.setInputCaps([e],[2e3]),e.addEventListener("input",()=>{var t=parseInt(e.value||500);n.resolution=t,n.changeAspectRatio(!0)}),$("#download-button").addEventListener("click",()=>{n.wilson.downloadFrame("a-lyapunov-fractal.png")}),t(),showPage()}export{load};