import{LyapunovFractal}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const applet=new LyapunovFractal($("#output-canvas")),generatingStringInputElement=$("#generating-string-input");applet.listenToInputElements([generatingStringInputElement],run),$("#generate-button").addEventListener("click",run);const resolutionInputElement=$("#resolution-input");function run(){var generatingString=(generatingStringInputElement.value||"AB").toUpperCase();applet.run({generatingString:generatingString})}applet.setInputCaps([resolutionInputElement],[2e3]),resolutionInputElement.addEventListener("input",()=>{var resolution=parseInt(resolutionInputElement.value||500);applet.resolution=resolution,applet.changeAspectRatio(!0)}),$("#download-button").addEventListener("click",()=>{applet.wilson.downloadFrame("a-lyapunov-fractal.png")}),run(),showPage()}export{load};