import{MagicCarpet}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const applet=new MagicCarpet($("#output-canvas")),gridSizeInputElement=$("#grid-size-input"),maxCageSizeInputElement=$("#max-cage-size-input"),uniqueSolutionCheckboxElement=(applet.setInputCaps([gridSizeInputElement],[100]),gridSizeInputElement.addEventListener("keydown",e=>{"Enter"===e.key&&run()}),maxCageSizeInputElement.addEventListener("keydown",e=>{"Enter"===e.key&&run()}),$("#unique-solution-checkbox"));function run(){var gridSize=parseInt(gridSizeInputElement.value||8),maxCageSize=parseInt(maxCageSizeInputElement.value||16),uniqueSolution=uniqueSolutionCheckboxElement.checked;applet.run({gridSize:gridSize,maxCageSize:maxCageSize,uniqueSolution:uniqueSolution})}$("#generate-button").addEventListener("click",run),$("#draw-solution-button").addEventListener("click",()=>{applet.drawSolution()}),$("#draw-rectangles-button").addEventListener("click",()=>{applet.drawSolution(!0)}),$("#download-button").addEventListener("click",()=>{applet.wilson.downloadFrame("a-magic-carpet.png")}),showPage()}export{load};