import{QuasiFuchsianGroups}from"./class.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const applet=new QuasiFuchsianGroups($("#output-canvas")),resolutionInputElement=$("#resolution-input"),highResolutionInputElement=$("#high-resolution-input"),maxDepthInputElement=$("#max-depth-input"),maxPixelBrightnessInputElement=$("#max-pixel-brightness-input");applet.setInputCaps([resolutionInputElement,highResolutionInputElement,maxDepthInputElement,maxPixelBrightnessInputElement],[500,3e3,1e3,500]),resolutionInputElement.addEventListener("input",()=>{applet.resolutionSmall=parseInt(resolutionInputElement.value||300),applet.resolutionLarge=3*parseInt(resolutionInputElement.value||300),applet.changeAspectRatio()}),$("#generate-button").addEventListener("click",async()=>{var imageSize=parseInt(highResolutionInputElement.value||1e3),maxDepth=parseInt(maxDepthInputElement.value||250),maxPixelBrightness=parseInt(maxPixelBrightnessInputElement.value||50);await applet.requestHighResFrame(imageSize,maxDepth,maxPixelBrightness),applet.wilson.downloadFrame("a-quasi-fuchsian-group.png")}),showPage()}export{load};