import{Applet}from"/scripts/src/applets.min.mjs";import{addTemporaryWorker}from"/scripts/src/main.min.mjs";import{Wilson}from"/scripts/wilson.min.mjs";class StrangeAttractor extends Applet{webWorker=null;image=[];brightnessScale=10;constructor(canvas){super(canvas);this.wilson=new Wilson(canvas,{renderer:"cpu",canvasWidth:1e3,canvasHeight:1e3,useFullscreen:!0,useFullscreenButton:!0,enterFullscreenButtonIconPath:"/graphics/general-icons/enter-fullscreen.png",exitFullscreenButtonIconPath:"/graphics/general-icons/exit-fullscreen.png"})}run({resolution,sigma,rho,beta,maximumSpeed}){this.wilson.changeCanvasSize(resolution,resolution),this.wilson.ctx.fillStyle="rgb(0, 0, 0)",this.wilson.ctx.fillRect(0,0,resolution,resolution),this.image=new Array(resolution*resolution);for(let i=0;i<resolution;i++)for(let j=0;j<resolution;j++)this.image[resolution*i+j]=0;this.webWorker=addTemporaryWorker("/applets/strange-attractors/scripts/worker.js"),this.webWorker.onmessage=e=>{var pixels=e.data[0],rgb=e.data[1];for(let i=0;i<pixels.length;i++){this.image[resolution*pixels[i][0]+pixels[i][1]]++;var brightnessAdjust=this.image[resolution*pixels[i][0]+pixels[i][1]]/this.brightnessScale;this.wilson.ctx.fillStyle=`rgb(${rgb[0]*brightnessAdjust}, ${rgb[1]*brightnessAdjust}, ${rgb[2]*brightnessAdjust})`,this.wilson.ctx.fillRect(pixels[i][1],pixels[i][0],1,1)}},this.webWorker.postMessage([resolution,sigma,rho,beta,maximumSpeed])}}export{StrangeAttractor};