import{ThurstonGeometry}from"./class.min.mjs";import{getE3RoomsData,getE3SpheresData,getH3SpheresData,getS3RoomsData,getS3SpheresData}from"./geometry-data.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const t=new ThurstonGeometry({canvas:$("#output-canvas")}),s=(t.run(getH3SpheresData()),$("#resolution-input")),a=(t.setInputCaps([s],[2e3]),s.addEventListener("input",()=>{var e=parseInt(s.value||500);t.changeResolution(e)}),{"e3-rooms":getE3RoomsData,"e3-spheres":getE3SpheresData,"s3-rooms":getS3RoomsData,"s3-spheres":getS3SpheresData,"h3-spheres":getH3SpheresData}),o=$("#scene-selector-dropdown");o.addEventListener("input",()=>{var e;"none"!==o.value&&(e=a[o.value],t.run(e()))}),$("#download-button").addEventListener("click",()=>{t.wilson.downloadFrame("a-thurston-geometry.png")}),showPage()}export{load};