import{WilsonsAlgorithm}from"./class.min.mjs";import{Applet}from"/scripts/src/applets.min.mjs";import{showPage}from"/scripts/src/load-page.min.mjs";import{$}from"/scripts/src/main.min.mjs";function load(){const t=new WilsonsAlgorithm({canvas:$("#output-canvas")});$("#generate-button").addEventListener("click",o);const n=$("#grid-size-input"),r=(t.setInputCaps([n],[200]),Applet.listenToInputElements([n],o),$("#download-button").addEventListener("click",()=>{t.wilson.downloadFrame("wilsons-algorithm.png")}),$("#toggle-maximum-speed-checkbox")),i=$("#no-borders-checkbox");function o(){var o=parseInt(n.value||50),s=r.checked,e=i.checked;t.run({gridSize:o,maximumSpeed:s,noBorders:e})}showPage()}export{load};