import anime from"../anime.min.js";import{pageShown}from"./loadPage.min.js";import{pageElement}from"./main.min.js";function loadImages(){for(const a of pageElement.querySelectorAll("img[data-src]")){a.style.opacity=0,a.src=a.getAttribute("data-src");var e=document.createElement("img");e.onload=()=>{anime({targets:a,opacity:1,duration:pageShown?300:10,delay:pageShown?0:50,easing:"easeInOutQuad"})},e.src=a.getAttribute("data-src")}}export{loadImages};