import { showPage } from "/scripts/src/load-page.mjs"
function getDerivativeString(t){var a=["f"];for(let r=0;r<t;r++)a.push("'");return a.push("(a)"),a.join("")}function load(){let t=[];for(let r=0;r<=10;r++)t.push(derivativeString=getDerivativeString(r));Page.Load.getDesmosData=()=>{return{"taylor-series":{bounds:{left:-10,right:10,bottom:-10,top:10},expressions:[{latex:String.raw`f(x) = \sin(x)`,color:purple},{latex:String.raw`\sum_{n = 0}^N \frac{D[n + 1]}{n!} (x-a)^n`,color:blue},{latex:String.raw`N = 5`,sliderBounds:{min:0,max:10,step:1}},{latex:String.raw`a = 0`},{latex:String.raw`(a, f(a))`,color:blue,pointSize:50,secret:!0},{latex:String.raw`D = [${t.join(", ")}]`,secret:!0}]},"taylor-series-comparison":{bounds:{left:-5,right:15,bottom:-10,top:10},expressions:[{latex:String.raw`f(x) = \sin(x)`,color:purple},{latex:String.raw`N = 5`,sliderBounds:{min:0,max:10,step:1}},{latex:String.raw`F_1(x) = \sum_{n = 0}^N \frac{D(0)[n + 1]}{n!} (x)^n`,color:blue},{latex:String.raw`F_2(x) = \sum_{n = 0}^N \frac{D(3\pi)[n + 1]}{n!} (x-3\pi)^n`,color:red},{latex:String.raw`f(10)`},{latex:String.raw`F_2(10)`},{latex:String.raw`F_1(10)`},{latex:String.raw`D(a) = [${t.join(", ")}]`,hidden:!0,secret:!0}]},"problematic-taylor-series":{bounds:{left:-2.5,right:2.5,bottom:-2.5,top:2.5},expressions:[{latex:String.raw`f(x) = \{x = 0: 0, e^{-\frac{1}{x^2}}\}`,color:purple},{latex:String.raw`\sum_{n = 0}^N \frac{D[n + 1]}{n!} (x-a)^n`,color:blue},{latex:String.raw`N = 5`,sliderBounds:{min:0,max:10,step:1}},{latex:String.raw`a = 0`},{latex:String.raw`(a, f(a))`,color:blue,secret:!0},{latex:String.raw`D = [${t.slice(0,6).join(", ")}]`,secret:!0}]}}},Page.Load.createDesmosGraphs(),showPage()}export{load};